import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';

@Component({
  selector: 'app-posts-create',
  templateUrl: './posts-create.component.html',
  styleUrls: ['./posts-create.component.css']
})
export class PostsCreateComponent implements OnInit {

  constructor() { }

  ngOnInit(): void {
  }

  onAddPost(postForm: NgForm) {
    if (postForm.invalid) {
      alert('Invalid!');
      return;
    }
    alert('Post ID: ' + postForm.value.enteredID + ' - Post Name: ' + postForm.value.enteredName);
  }

}
<form (submit)="onAddPost(addPostForm)" #addPostForm="ngForm">
    <input type="text"
           ngModel
           name="enteredID"
           required
           minLength="1"
           pattern="^[a-zA-Z0-9-_]{1,}$"
           placeholder="Post ID"
           #enteredID="ngModel"/>
    <input type="text"
           ngModel
           name="enteredName"
           required
           minLength="1"
           pattern="^[a-zA-Z0-9-_]{1,}$"
           placeholder="Post Name"
           #enteredName="ngModel"/>
    <button type="submit">Add Post</button>
</form>
