import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Subject } from 'rxjs';
import { AuthServiceService } from '../auth/auth-service.service'; // Import the AuthServiceService to get the token

@Injectable({ providedIn: 'root' })
export class PostsServiceService {

  private postsDisplay: { _id: string; id: string; name: string; _v: string }[] = [];
  private updatedPostsDisplay = new Subject<{ _id: string; id: string; name: string; _v: string }[]>();

  constructor(private http: HttpClient, private authService: AuthServiceService) { } // Inject AuthServiceService

  private getAuthHeaders() {
    const token = this.authService.getToken();

    // Logging the token for debugging purposes
    console.log("Retrieved token:", token);

    if(!token) {
      // If the token is not present, throw an error or handle this case accordingly
      throw new Error('Token is not available');
    }

    return {
      headers: new HttpHeaders({
        'Authorization': 'Bearer ' + token
      })
    };
  }

  addPost_service(pid: string, pname: string) {
    this.http.post<{ message: string; post: any }>('https://localhost:3000/api/posts', 
      { _id: pid, id: pid, name: pname }, this.getAuthHeaders())
      .subscribe((response) => {
        this.postsDisplay.push(response.post);
        this.updatedPostsDisplay.next([...this.postsDisplay]);
      });
  }

  getPost_service() {
    this.http.get<{ message: string; posts: any }>('https://localhost:3000/api/posts', this.getAuthHeaders())
      .subscribe((postResponse) => {
        this.postsDisplay = postResponse.posts;
        this.updatedPostsDisplay.next([...this.postsDisplay]);
      });
  }

  deletePost_service(postId: string) {
    this.http.delete('https://localhost:3000/api/posts/' + postId, this.getAuthHeaders())
      .subscribe(() => {
        const updatedPostsDeleted = this.postsDisplay.filter(post => post._id !== postId);
        this.postsDisplay = updatedPostsDeleted;
        this.updatedPostsDisplay.next([...this.postsDisplay]);
      });
  }

  getUpdateListener() {
    return this.updatedPostsDisplay.asObservable();
  }
}
