import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { AuthServiceService } from '../../auth-service.service';

@Component({
    selector: 'app-login',
    templateUrl: './login.component.html',
    styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {

  public option: string;
  public username: string = '';  
  public password: string = '';  
  public formError: string | null = '';  

  constructor(private authservice: AuthServiceService, private router: Router) {
      this.option = this.router.url;
  }

  ngOnInit(): void { }

  onLogin(loginForm: NgForm): void {
    if (loginForm.invalid) {
        this.formError = "Please ensure the form is filled out correctly.";
        return;
    }

    this.formError = null;  
    if (this.option === '/login') {
      this.authservice.login(this.username, this.password).subscribe(() => {
        // Navigate to the posts display component after successful login
        this.router.navigate(['/']);
      });
    } else {
        this.authservice.signup(this.username, this.password);
    }
  }
}  
<form (submit)="onLogin(loginForm)" #loginForm="ngForm">

    <!-- Username Input -->
    <input type="text" 
           name="username" 
           [(ngModel)]="username" 
           placeholder="Username"
           required
           pattern="^[\w.-]+@([-a-zA-Z0-9]+\.)+[a-zA-Z]{2,4}$"
           #usernameRef="ngModel"/>
           <div *ngIf="usernameRef?.invalid && (usernameRef?.dirty || usernameRef?.touched)">
            <div *ngIf="usernameRef?.errors?.['required']">Username is required.</div>
            <div *ngIf="usernameRef?.errors?.['pattern']">Invalid email format.</div>
        </div>
    <br>

    <!-- Password Input -->
    <input type="password" 
           name="password" 
           [(ngModel)]="password" 
           placeholder="Password"
           required
           minlength="8"
           pattern="^([a-zA-Z0-9@*#]{8,15})$"
           #passwordRef="ngModel"/>
           <div *ngIf="passwordRef?.invalid && (passwordRef?.dirty || passwordRef?.touched)">
            <div *ngIf="passwordRef?.errors?.['required']">Password is required.</div>
            <div *ngIf="passwordRef?.errors?.['minlength']">Password should be at least 8 characters long.</div>
            <div *ngIf="passwordRef?.errors?.['pattern']">Invalid password format. Use only letters, numbers, and special characters (@, *, #).</div>
        </div>
    <br>

    <button type="submit">{{option}}</button>
</form>
